function toggle(e,o){if(e)return e.classList.toggle("active",o)}const articles=document.querySelectorAll("article");articles.forEach((e=>{const o=e.querySelector(".body");toggle(o,!1),e.onclick=e=>{toggle(o)||e.preventDefault()}}));const links=document.querySelectorAll("#sidebar a");links.forEach((e=>{const o=e.getAttribute("href").substring(1);e.onclick=()=>{const e=document.getElementById(o),t=e.closest("article");if(t){toggle(t.querySelector(".body"),!0)}setTimeout((()=>{(t||e).scrollIntoView({behavior:"smooth"})}),500)};document.querySelectorAll(`.body a[href="#${o}"]`).forEach((o=>{o.onclick=()=>{e.click()}}))}));const show=document.querySelector("button.control.show"),hide=document.querySelector("button.control.hide");show.onclick=()=>{articles.forEach((e=>{toggle(e.querySelector(".body"),!0)}))},hide.onclick=()=>{articles.forEach((e=>{toggle(e.querySelector(".body"),!1)}))},window.onload=()=>{const e=window.location.hash.substring(1);if(e){document.querySelector(`#sidebar a[href="#${e}"]`).click()}};
